import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Home.css';

// Import all images in the images directory
function importAll(r) {
  let images = {};
  r.keys().map((item, index) => { images[item.replace('./', '')] = r(item); });
  return images;
}

// Import all images from the images directory
const images = importAll(require.context('./images', false, /\.(png|jpe?g|svg)$/));

function ProductList() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Fetch data from the backend when the component mounts
    async function fetchData() {
      try {
        console.log('Fetching data...'); // Log that data fetching has started
        const response = await axios.get('http://localhost:5000/products'); // Make GET request to backend endpoint
        console.log('Data received:', response.data); // Log the data received from the backend
        setProducts(response.data); // Set products state with data received from backend
      } catch (error) {
        console.error('Error fetching data:', error); // Log any errors that occur during data fetching
      }
    }

    fetchData(); // Call the fetchData function
  }, []);

  return (
    <div className='home'>
      <h1>Welcome to Our Menu</h1>
      <div className='menu'>
        {products.map((item) => (
          <div key={item.id} className='item'>
            {/* Dynamically bind the imported images based on the image names */}
            <img src={images[item.image]} alt={item.image} className='item-image' />
            <h2>{item.name}</h2>
            <p>{item.description}</p>
            <p className='price'>Price: {item.price}</p>
            <button>Add to Cart</button>
          </div>
        ))}
      </div>
      {/* Include the AnotherComponent here or anywhere else you want */}
      {/* <AnotherComponent cartItems={cartItems} /> */}
    </div>
  );
}

export default ProductList;
