import React, { useState, useEffect } from 'react';
import Nav from './Nav';
import './Cart.css';

function Cart() {
  // State variables for cart items, total price, and dynamically imported images
  const [cart, setCart] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [imageImports, setImageImports] = useState({});

  // useEffect hook to load cart items and images on component mount
  useEffect(() => {
    // Load cart items from local storage
    const storedCart = JSON.parse(localStorage.getItem('cart'));
    if (storedCart) {
      setCart(storedCart);
      updateTotalPrice(storedCart); // Calculate total price
      importImages(storedCart); // Dynamically import images
    }
  }, []);

  // Function to remove an item from the cart
  const removeFromCart = (index) => {
    const updatedCart = [...cart];
    updatedCart.splice(index, 1);
    setCart(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart)); // Update local storage
    updateTotalPrice(updatedCart); // Recalculate total price
  };

  // Function to calculate total price of cart items
  const updateTotalPrice = (cartItems) => {
    const total = cartItems.reduce((total, item) => total + parseFloat(item.price), 0);
    setTotalPrice(total);
  };

  // Function to dynamically import images for cart items
  const importImages = async (cartItems) => {
    const imports = {};
    await Promise.all(
      cartItems.map(async (item) => {
        try {
          // Dynamically import image based on item's image path
          const { default: image } = await import(`./images/${item.image}`);
          imports[item.id] = image;
          console.log(`Successfully imported image for ${item.name}:`, image);
        } catch (error) {
          console.error(`Error importing image for ${item.name}:`, error);
        }
      })
    );
    setImageImports(imports); // Update state with imported images
  };

  // Function to place an order
  const placeOrder = () => {
    // Display prompt if cart is not empty
    if (cart.length > 0) {
      const confirmation = window.confirm("Your order is placed!");
      if (confirmation) {
        // Clear cart and update total price
        setCart([]);
        setTotalPrice(0);
        localStorage.removeItem('cart'); // Remove cart from local storage
      }
    } else {
      alert("Your cart is empty!");
    }
  };

  return (
    <div className="cart-container">
      {/* Navigation bar */}
      <Nav links={Nav} />

      <div>
        <h1 className="cart-header">Cart Menu</h1>

        {/* List of cart items */}
        <ul className="cart-list">
          {cart && cart.map((item, index) => (
            <li key={index} className="cart-item">
              {/* Display dynamically imported image */}
              {imageImports[item.id] && (
                <img src={imageImports[item.id].default} alt={item.name} className='item-image' />
              )}

              {/* Item details: name, price, quantity */}
              <div className="item-details">
                <span className="item-name">{item.name}</span>
                <span className="item-price">${item.price}</span>
                <span className="item-quantity">Quantity: {item.quantity}</span>
              </div>

              {/* Button to remove item from cart */}
              <button className="remove-button" onClick={() => removeFromCart(index)}>Remove</button>
            </li>
          ))}
        </ul>

        {/* Total price of all items in the cart */}
        <p className="total-price">Total: ${totalPrice}</p>

        {/* Button to place order */}
        <button onClick={placeOrder}>Place Order</button>
      </div>
    </div>
  );
}

export default Cart;
