import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Home.css';
import Nav from './Nav.js';
import Cart from './Cart';

function ProductList() {
  const [products, setProducts] = useState([]);
  const [imageImports, setImageImports] = useState({});
  const [cart, setCart] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get('http://localhost:5000/products');
        setProducts(response.data);
        const importedImages = {};
        await Promise.all(response.data.map(async (item) => {
          const imageModule = await import(`./images/${item.image}`);
          importedImages[item.id] = imageModule.default;
        }));
        setImageImports(importedImages);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }

    fetchData();
  }, []);

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem('cart'));
    if (storedCart) {
      setCart(storedCart);
    }
  }, []);

  const addToCart = (product) => {
    const existingProductIndex = cart.findIndex((item) => item.id === product.id);
    if (existingProductIndex !== -1) {
      const updatedCart = [...cart];
      updatedCart[existingProductIndex].quantity += 1;
      setCart(updatedCart); // Update the cart state
      localStorage.setItem('cart', JSON.stringify(updatedCart)); // Update local storage with updatedCart
    } else {
      const updatedCart = [...cart, { ...product, quantity: 1 }];
      setCart(updatedCart); // Update the cart state
      localStorage.setItem('cart', JSON.stringify(updatedCart)); // Update local storage with updatedCart
    }
  };

  return (
    <div>
      <Nav links={Nav} />
      <div className='home'>
        <h1>Non-Vegetarian Specials</h1>
        {/* No need to display products here */}
      </div>
      <Cart cart={cart} />
    </div>
  );
}

export default ProductList;
