import React, { useState } from 'react';
import './Home.css';

import chickenCurryImage from './images/chicken_curry.jpg';
import grilledSalmonImage from './images/grilled_salmon.jpg';
import chickenBurgerImage from './images/chicken_burger.jpg';

function Home() {
    const nonVegItems = [
        { id: 1, name: 'Chicken Curry', description: 'Spicy chicken curry', price: '$10.99', image: chickenCurryImage },
        { id: 2, name: 'Grilled Salmon', description: 'Freshly grilled salmon fillet', price: '$12.99', image: grilledSalmonImage },
        { id: 3, name: 'Chicken Burger', description: 'Juicy beef burger with cheese and veggies', price: '$8.99', image: chickenBurgerImage },
    ];

    const [cartItems, setCartItems] = useState([]);

    const addToCart = (item) => {
        setCartItems([...cartItems, item]);
    };

    return (
        <div className='home'>
            <h1>Welcome to Our Menu</h1>
            <div className='menu'>
                {nonVegItems.map((item) => (
                    <div key={item.id} className='item'>
                        <img src={item.image} alt={item.name} className='item-image' />
                        <h2>{item.name}</h2>
                        <p>{item.description}</p>
                        <p className='price'>Price: {item.price}</p>
                        <button onClick={() => addToCart(item)}>Add to Cart</button>
                    </div>
                ))}
            </div>
           
        </div>
    );
}

export default Home;
