import React, { useState } from 'react';
import './Log.css';
import logo from './images/logo.jpg';


function Log() {
  const [showLogin, setShowLogin] = useState(true); // Initially show the Login component

  const toggleForm = () => {
    setShowLogin((prevShowLogin) => !prevShowLogin);
  };

  return (
    <div className="Log">
      <img src={logo} alt="Logo" className="full-window-image" />
      
      <div className="navbar">
        <ul>
          <li>
            <button onClick={toggleForm}>Login</button> {/* Toggle to show Login */}
          </li>
          <li>
            <button onClick={toggleForm}>Signup</button> {/* Toggle to show Signup */}
          </li>
        </ul>
      </div>

      {/* Conditionally render either Login or Signup based on showLogin state */}
    </div>
  );
}

export default Log;
