import React, { useState, useEffect } from 'react';
import { Route, Routes, Link } from 'react-router-dom';
import axios from 'axios';
import './Home.css';
import Nav from './Nav.js';
import Cart from './Cart'; // Import the Cart component

function ProductList() {
  const [products, setProducts] = useState([]);
  const [imageImports, setImageImports] = useState({});
  const [cart, setCart] = useState([]); // State to store selected products in the cart

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get('http://localhost:5000/products');
        setProducts(response.data);
        // Dynamically import images
        const importedImages = {};
        await Promise.all(response.data.map(async (item) => {
          const imageModule = await import(`./images/${item.image}`);
          importedImages[item.id] = imageModule.default;
        }));
        setImageImports(importedImages);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }

    fetchData();
  }, []);

  // Function to add product to cart
  const addToCart = (product) => {
    // Check if the product is already in the cart
    const existingProductIndex = cart.findIndex((item) => item.id === product.id);
    if (existingProductIndex !== -1) {
      // If the product is already in the cart, update the quantity
      const updatedCart = [...cart];
      updatedCart[existingProductIndex].quantity += 1;
      setCart(updatedCart);
    } else {
      // If the product is not in the cart, add it with quantity 1
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  return (
    <div>
      <Nav links={Nav} />
      <div className='home'>
        <h1>Non-Vegetarian Specials</h1>
        <div className='menu'>
          {products.map((item) => (
            <div key={item.id} className='item'>
              {/* Use dynamically imported image */}
              {imageImports[item.id] && (
                <img src={imageImports[item.id]} alt={item.name} className='item-image' />
              )}
              <h2>{item.name}</h2>
              <p>{item.description}</p>
              <p className='price'>Price: {`\u20B9`}{item.price}</p>
              <button onClick={() => addToCart(item)}>Add to Cart</button> {/* Add onClick handler */}
            </div>
          ))}
        </div>
      </div>
      {/* Pass cart state as a prop to the Cart component */}
      <Cart cart={cart} />
    </div>
  );
}

export default ProductList;
