import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Home.css';

function ProductList() {
  const [products, setProducts] = useState([]);
  const [imageImports, setImageImports] = useState({});

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get('http://localhost:5000/products');
        setProducts(response.data);
        // Dynamically import images
        const importedImages = {};
        response.data.forEach(item => {
          import(`./images/${item.image}`).then(image => {
            importedImages[item.id] = image.default;
            console.log(importedImages);
            setImageImports(importedImages);
          });
        });
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }

    fetchData();
  }, []);

  return (
    <div className='home'>
      <h1>Welcome to Our Menu</h1>
      <div className='menu'>
        {products.map((item) => (
          <div key={item.id} className='item'>
            {/* Use dynamically imported image */}
            {imageImports[item.id] && (
              <img src={`./images/${imageImports[item.id]}`} alt={item.name} className='item-image' />
            )}
            <h2>{item.name}</h2>
            <p>{item.description}</p>
            <p className='price'>Price: {item.price}</p>
            <button>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProductList;
