import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import axios from 'axios';
import './Home.css';
import Veg from './Veg';
import NonVeg from './Home';
import Tiffins from './Tiffins';
import AboutUs from './AboutUs';

function ProductList() {
  const [products, setProducts] = useState([]);
  const [imageImports, setImageImports] = useState({});

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get('http://localhost:5000/products');
        setProducts(response.data);
        // Dynamically import images
        const importedImages = {};
        await Promise.all(response.data.map(async (item) => {
          const imageModule = await import(`./images/${item.image}`);
          importedImages[item.id] = imageModule.default;
        }));
        setImageImports(importedImages);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }

    fetchData();
  }, []);

  return (
    <div className='home'>
      <h1>Welcome to Our Menu</h1>
      <div className='menu'>
        {products.map((item) => (
          <div key={item.id} className='item'>
            {/* Use dynamically imported image */}
            {imageImports[item.id] && (
              <img src={imageImports[item.id]} alt={item.name} className='item-image' />
            )}
            <h2>{item.name}</h2>
            <p>{item.description}</p>
            <p className='price'>Price: {item.price}</p>
            <button>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
}


function App() {
  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/veg">Veg</Link>
            </li>
            <li>
              <Link to="/non-veg">Non-Veg</Link>
            </li>
            <li>
              <Link to="/tiffins">Tiffins</Link>
            </li>
            <li>
              <Link to="/about-us">About Us</Link>
            </li>
          </ul>
        </nav>

        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/veg" element={<Veg />} />
          <Route path="/non-veg" element={<NonVeg />} />
          <Route path="/tiffins" element={<Tiffins />} />
          <Route path="/about-us" element={<AboutUs />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
