// Signup.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Signup.css'; // Import the CSS file for Signup component
import logo from './images/logo.jpg'; // Import the logo image

function Signup() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [rePassword, setRePassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validation checks for empty fields
    if (!username.trim() || !password.trim() || !rePassword.trim()) {
      setError('All fields are mandatory');
      return;
    }

    if (password !== rePassword) {
      setError('Passwords do not match');
      return;
    }

    try {
      // Make a POST request to store user information in MongoDB
      await axios.post('http://localhost:5000/signup', { username, password });
      navigate('/login');
    } catch (error) {
      setError('Error signing up');
      console.error('Error signing up:', error);
    }
  };

  return (
    <div className="Signup" style={{ backgroundImage: `url(${logo})`, backgroundSize: 'cover', backgroundPosition: 'center', backgroundAttachment: 'fixed' }}>
      <div className="Signup-container">
        <h2>Signup</h2>
        <form onSubmit={handleSubmit}>
          {/* Rest of the Signup component */}
        </form>
        <p>Already have an account? <a href="/login">Login</a></p>
      </div>
    </div>
  );
}

export default Signup;
